import React, { useState } from "react";
import "./App.css"; // Assumindo que seu CSS está em App.css

function App() {
  const [isOpenVegan, setIsOpenVegan] = useState(false);
  const [isOpenFrance, setIsOpenFrance] = useState(false);

  const toggleDropdownVegan = () => {
    setIsOpenVegan(!isOpenVegan);
  };

  const toggleDropdownFrance = () => {
    setIsOpenFrance(!isOpenFrance);
  };

  return (
    <div className="container-drop">
      <h1>Opções de Menu para Grupos</h1>

      {/* Container de dropdowns */}
      <div className="dropdown-container">
        {/* Dropdown Menu Vegan */}
        <div className="dropdown">
          <button className="dropdown-button" onClick={toggleDropdownVegan}>
            Menu Vegan
          </button>
          {isOpenVegan && (
            <ul className="dropdown-menu">
              <li>Entrada: Bruschetta de Abacate e Bolinho de Grão-de-Bico</li>
              <li>Principal: Risoto de Cogumelos e Aspargos</li>
              <li>Sobremesa: Torta de Chocolate com Castanhas</li>
              <li>Bebida: Suco Verde Detox ou cerveja vegana</li>
            </ul>
          )}
        </div>

        {/* Dropdown Menu França */}
        <div className="dropdown">
          <button className="dropdown-button" onClick={toggleDropdownFrance}>
            Menu França
          </button>
          {isOpenFrance && (
            <ul className="dropdown-menu">
              <li>Entrada: Quiche Lorraine e Escargots à la Bourguignonne</li>
              <li>Principal: Coq au Vin</li>
              <li>Sobremesa: Tarte Tatin</li>
              <li>Bebida: Vin de Bordeaux</li>
            </ul>
          )}
        </div>

        {/* Outros menus (você pode adicionar mais como os exemplos acima) */}
      </div>
    </div>
  );
}

export default App;
