import React, { useState } from 'react';
import './Menu.css'; 

const Menu = () => {

    const pratos = [
        { id: 1, nome: 'one', descricao: 'Salmão ao molho de teraki', imagem: '../assets/prato1.jpg'},
        { id: 2, nome: 'Prato 2', descricao: 'Descrição do prato 2', imagem: 'url_da_imagem_2' },
        // ... outros pratos
      ];

      const takeAway = [
        { id: 7, nome: 'Take-Away 1', descricao: 'Descrição do prato take-away 1', imagem: 'url_da_imagem_7' },
        { id: 8, nome: 'Take-Away 2', descricao: 'Descrição do prato take-away 2', imagem: 'url_da_imagem_8' },
      ];


      const [imagemAmpliada, setImagemAmpliada] = useState(null);

      const abrirImagem = (imagem) => {
        setImagemAmpliada(imagem);
      };

      const fecharModal = () => {
        setImagemAmpliada(null);
      };

    return (
        <div>
            <h1>The Nordic Table</h1>
            <div className='divp'>
            <p>Explore nosso menu, onde o frescor nórdico se encontra com o requinte francês, em pratos criados 
            para surpreender e encantar.</p>
            </div>

            // Menu 
            <div className="grid-menu">
        {pratos.map(prato => (
          <div key={prato.id} className="card-prato">
            <img
              src={prato.imagem}
              alt={prato.nome}
              onClick={() => abrirImagem(prato.imagem)}
              style={{ cursor: 'pointer' }}
            />
            <h3>{prato.nome}</h3>
            <p>{prato.descricao}</p>
          </div>
        ))}
      </div>


      //Menu Take away

      <h2>Take-Away</h2>
      <div className="grid-takeaway">
        {takeAway.map(prato => (
          <div key={prato.id} className="card-prato">
            <img
              src={prato.imagem}
              alt={prato.nome}
              onClick={() => abrirImagem(prato.imagem)}
              style={{ cursor: 'pointer' }}
            />
            <h3>{prato.nome}</h3>
            <p>{prato.descricao}</p>
          </div>
        ))}
      </div>


        </div>

    );

};

export default Menu;
