import React, { useState } from 'react';
import './Menu.css'; 

import imagem1 from './assets/imagem1.jpg';
import imagem2 from './assets/imagem2.jpg';



const Menu = () => {

    const pratos = [
        { id: 1, nome: 'Risoto de abóbora', descricao: 'Risoto de abóbora e frango', imagem: '../assets/prato1.jpg'},
        { id: 2, nome: 'Salmão Teraki', descricao: 'Salmão ao molho de teraki', imagem: 'u../assets/prato2.jpg' },
        // ... outros pratos
      ];

      const takeAway = [
        { id: 7, nome: 'Take-Away 1', descricao: 'Descrição do prato take-away 1', imagem: 'url_da_imagem_7' },
        { id: 8, nome: 'Take-Away 2', descricao: 'Descrição do prato take-away 2', imagem: 'url_da_imagem_8' },
      ];


      const [imagemAmpliada, setImagemAmpliada] = useState(null);

      const abrirImagem = (imagem) => {
        setImagemAmpliada(imagem);
      };

      const fecharModal = () => {
        setImagemAmpliada(null);
      };

    return (
        <div>
            <h1>The Nordic Table</h1>
            <div className='divp'>
            <p>Explore nosso menu, onde o frescor nórdico se encontra com o requinte francês, em pratos criados 
            para surpreender e encantar.</p>
            </div>


            <div className="grid-menu">
        {pratos.map(prato => (
          <div key={prato.id} className="card-prato">
            <img
              src={prato.imagem}
              onClick={() => abrirImagem(prato.imagem)}
              style={{ cursor: 'pointer' }}
            />
            <h3>{prato.nome}</h3>
            <p>{prato.descricao}</p>
          </div>
        ))}
      </div>



      <h2>Take-Away</h2>
      <div className="grid-takeaway">
        {takeAway.map(prato => (
          <div key={prato.id} className="card-prato">
            <img
              src={prato.imagem}
              alt={prato.nome}
              onClick={() => abrirImagem(prato.imagem)}
              style={{ cursor: 'pointer' }}
            />
            <h3>{prato.nome}</h3>
            <p>{prato.descricao}</p>
          </div>
        ))}

        {/* Modal para exibir a imagem ampliada */}

      {imagemAmpliada && (
        <div className="modal" onClick={fecharModal}>
          <div className="modal-content">
            <span className="close" onClick={fecharModal}>&times;</span>
            <img src={imagemAmpliada} alt="Imagem ampliada" />
          </div>
        </div>
      )}

      </div>

     </div>

    );

};

export default Menu;
